document.addEventListener('DOMContentLoaded',()=>{const links=document.querySelectorAll('a[href^="#"]');for(const link of links){link.addEventListener('click',(e)=>{e.preventDefault();const targetId=link.getAttribute('href');const targetElement=document.querySelector(targetId);if(targetElement){window.scrollTo({top:targetElement.offsetTop-70,behavior:'smooth'});}});}const sections=document.querySelectorAll('section');window.addEventListener('scroll',()=>{const scrollPosition=window.scrollY+200;sections.forEach(section=>{const sectionTop=section.offsetTop;const sectionHeight=section.offsetHeight;if(scrollPosition>=sectionTop&&scrollPosition<sectionTop+sectionHeight){section.classList.add('active');}});});const loadingScreen=document.getElementById('loading-screen');if(loadingScreen){window.addEventListener('load',()=>{setTimeout(()=>{loadingScreen.style.opacity='0';loadingScreen.style.visibility='hidden';document.body.classList.remove('no-scroll');},600);});}});